import { Platform } from 'react-native';
import { 
  auth, 
  GoogleAuthProvider, 
  signInWithPopup, 
  signInWithCredential,
  signInWithEmailAndPassword,
  signInAnonymously as firebaseSignInAnonymously
} from '../config/firebase';

class AuthService {
  private static instance: AuthService;
  private webClientId = '693837443791-b3g7l5rj77vt4p5fk7v1l9ra2c8g1m8q.apps.googleusercontent.com';

  private constructor() {}

  public static getInstance(): AuthService {
    if (!AuthService.instance) {
      AuthService.instance = new AuthService();
    }
    return AuthService.instance;
  }

  async signInWithGoogle() {
    try {
      const provider = new GoogleAuthProvider();
      provider.addScope('profile');
      provider.addScope('email');
      return await signInWithPopup(auth, provider);
    } catch (error: any) {
      console.error('Google Sign In Error:', error);
      throw new Error(error.message || 'Errore durante il login con Google');
    }
  }

  async signInWithEmailAndPassword(email: string, password: string) {
    try {
      return await signInWithEmailAndPassword(auth, email, password);
    } catch (error: any) {
      throw new Error(error.message || 'Errore durante il login con email e password');
    }
  }

  async signInAnonymously() {
    try {
      return await firebaseSignInAnonymously(auth);
    } catch (error: any) {
      throw new Error(error.message || 'Errore durante il login anonimo');
    }
  }

  async signOut() {
    try {
      await auth.signOut();
    } catch (error: any) {
      throw new Error(error.message || 'Errore durante il logout');
    }
  }
}

export default AuthService;
